mapper.select:
select count(b.documentNum) from ( select d.*,em1.receiver,em1.documentName,em1.estimateNum,ds.name stateName from (select * from documentMaster where systemNum=2) d join estimateMaster em1 on (d.documentNum = em1.documentNum) join documentState ds on (d.state = ds.state) ) b
where b.userNum=#{userNum} or b.state='app'
order by ${option}

select b.documentNum documentNumB, b.estimateNum estimateNumB, b.documentTypeName documentTypeNameB, b.nextDocumentTypeName nextDocumentTypeNameB, b.receiver receiverB, b.documentName documentNameB, b.updateDate updateDateB, b.userNum userNumB, b.userName userNameB, b.userDepartment userDepartmentB, b.userPosition userPositionB, b.state stateB, b.stateName stateNameB from ( select d.*,em1.receiver,em1.documentName,em1.estimateNum,ds.name stateName from (select * from documentMaster where systemNum=2) d join estimateMaster em1 on (d.documentNum = em1.documentNum) join documentState ds on (d.state = ds.state) ) b
where b.userNum=#{userNum} or b.state='app'
order by ${option}

mapper.insert:
insert into documentMaster ( documentNum, systemNum, documentTypeName, nextDocumentTypeName, userNum, userName, userDepartment, userPosition, workflowNum, state, updateDate, updater ) values( #{documentNum}, #{systemNum}, #{documentTypeName}, #{nextDocumentTypeName}, #{userNum}, #{userName}, #{userDepartment}, #{userPosition},
null,
#{workflowNum},
#{state}, CURRENT_TIMESTAMP, #{updater} );
insert into estimateMaster( documentNum, receiver, documentName, estimateNum )values( #{documentNum}, #{receiver}, #{documentName}, #{estimateNum} );
insert into billSheet1 ( documentNum, userNum, userName, userDepartment, userPosition, billDate, supplier, address, post, phoneNumber, representative, stamp, stampFileName, logoFileName, receiver, documentName, payCondition, deadline, bankName, branchName, accountName, hurigana, accountNumber, depositeClassification, note, sum, tax, sumWithTax, sumWithTax2, comment, updateDate, updater ) values ( #{documentNum}, #{userNum}, #{userName}, #{userDepartment}, #{userPosition}, #{billDate}, #{supplier}, #{address}, #{post}, #{phoneNumber}, #{representative}, #{stamp}, #{stampFileName}, #{logoFileName}, #{receiver}, #{documentName}, #{payCondition}, #{deadline}, #{bankName}, #{branchName}, #{accountName}, #{hurigana}, #{accountNumber}, #{depositeClassification}, #{note}, #{sum}, #{tax}, #{sumWithTax}, #{sumWithTax2}, #{comment}, CURRENT_TIMESTAMP, #{updater} )

mapper.update:
update documentMaster set updateDate = CURRENT_TIMESTAMP, updater = #{updater} where documentNum = #{documentNum};
update estimateMaster set receiver = #{receiver}, documentName = #{documentName} where documentNum = #{documentNum};
update billSheet1 set userNum = #{userNum}, userName = #{userName}, userDepartment = #{userDepartment}, userPosition = #{userPosition}, billDate = #{billDate}, supplier = #{supplier}, address = #{address}, post = #{post}, phoneNumber = #{phoneNumber}, representative = #{representative}, stamp = #{stamp}, stampFileName = #{stampFileName}, logoFileName = #{logoFileName}, receiver = #{receiver}, documentName = #{documentName}, payCondition = #{payCondition}, deadline = #{deadline}, bankName = #{bankName}, branchName = #{branchName}, accountName = #{accountName}, hurigana = #{hurigana}, accountNumber = #{accountNumber}, depositeClassification = #{depositeClassification}, note = #{note}, sum = #{sum}, tax = #{tax}, sumWithTax = #{sumWithTax}, sumWithTax2 = #{sumWithTax2}, comment = #{comment}, updateDate = CURRENT_TIMESTAMP, updater = #{updater} where documentNum = #{documentNum}

