mapper.select:
select count(b.documentNum) from ( select d.*,em1.receiver,em1.documentName,em1.estimateNum,ds.name stateName from (select * from documentMaster where systemNum=2) d join estimateMaster em1 on (d.documentNum = em1.documentNum) join documentState ds on (d.state = ds.state) ) b
where b.userNum=#{userNum} or b.state='app'
order by ${option}

select b.documentNum documentNumB, b.estimateNum estimateNumB, b.documentTypeName documentTypeNameB, b.nextDocumentTypeName nextDocumentTypeNameB, b.receiver receiverB, b.documentName documentNameB, b.updateDate updateDateB, b.userNum userNumB, b.userName userNameB, b.userDepartment userDepartmentB, b.userPosition userPositionB, b.state stateB, b.stateName stateNameB, b.approvedDate approvedDateB from ( select d.*,em1.receiver,em1.documentName,em1.estimateNum,ds.name stateName from (select * from documentMaster where systemNum=2) d join estimateMaster em1 on (d.documentNum = em1.documentNum) join documentState ds on (d.state = ds.state) ) b
where b.userNum=#{userNum}
order by ${option}

mapper.insert:
insert into documentMaster (
	documentNum, systemNum, documentTypeName, nextDocumentTypeName,
	userNum, userName, userDepartment, userPosition,
	workflowNum, 
	state, updateDate, updater
)
values(
	#{documentNum}, #{systemNum}, #{documentTypeName}, #{nextDocumentTypeName},
	#{userNum}, #{userName}, #{userDepartment}, #{userPosition},

		null,
		#{workflowNum},

	#{state}, CURRENT_TIMESTAMP, #{updater}
);

insert into estimateMaster(
	documentNum, receiver, documentName,
	estimateNum
)values(
	#{documentNum}, #{receiver}, #{documentName},
	null#{estimateNum}
)

mapper.update:
update documentMaster set
	updateDate = CURRENT_TIMESTAMP,
	updater = #{updater}
where 
	documentNum = #{documentNum};

update estimateMaster set
	receiver = #{receiver},
	documentName = #{documentName}
where
	documentNum = #{documentNum}

update billSolution set userNum = #{userNum}, userName = #{userName}, userDepartment = #{userDepartment}, userPosition = #{userPosition}, billDate = #{billDate}, stamp = #{stamp}, stampFileName = #{stampFileName}, logoFileName = #{logoFileName},
receiver = #{receiver}, address = #{address}, documentName = #{documentName}, payCondition = #{payCondition}, deadline = #{deadline}, bankName = #{bankName}, branchName = #{branchName}, accountName = #{accountName}, hurigana = #{hurigana}, accountNumber = #{accountNumber}, depositeClassification = #{depositeClassification}, note = #{note}, sum = #{sum}, taxRate = #{taxRate}, tax = #{tax}, sumWithTax = #{sumWithTax}, sumWithTax2 = #{sumWithTax2}, comment = #{comment},
updateDate = CURRENT_TIMESTAMP, updater = #{updater} where documentNum = #{documentNum};
update billSolutionItems set item = #{item.item}, itemName = #{item.itemName}, amount = ${item.amount}, unit = #{item.unit}, unitPrice = ${item.unitPrice}, price = ${item.price} where documentNum=#{documentNum} and rowNum = #{item.rowNum};

update billC set userNum = #{userNum}, userName = #{userName}, userDepartment = #{userDepartment}, userPosition = #{userPosition}, billDate = #{billDate},
stamp = #{stamp}, stampFileName = #{stampFileName}, logoFileName = #{logoFileName},
receiver = #{receiver}, address = #{address}, documentName = #{receiver}, payCondition = #{payCondition}, deadline = #{deadline}, itemTitle = #{itemTitle}, bankName = #{bankName}, branchName = #{branchName}, accountName = #{accountName}, hurigana = #{hurigana}, accountNumber = #{accountNumber}, depositeClassification = #{depositeClassification}, note = #{note}, sum = #{sum}, taxRate = #{taxRate}, tax = #{tax}, commissionRate = #{commissionRate}, commission = #{commission}, sumWithTax = #{sumWithTax}, sumWithTax2 = #{sumWithTax2}, updateDate = CURRENT_TIMESTAMP, updater = #{updater} where documentNum = #{documentNum};
update billCItems set itemName = #{item.itemName}, unitPrice = ${item.unitPrice}, amount = ${item.amount}, price = ${item.price} where documentNum=#{documentNum} and rowNum = #{item.rowNum};
