mapper.select:
select count(e.documentNum) from ( select d.*,em1.receiver,em1.documentName,ds.name stateName from (select * from documentMaster where systemNum=1) d join estimateMaster em1 on (d.documentNum = em1.documentNum) join documentState ds on (d.state = ds.state) ) e left outer join ( select d.*,em1.receiver,em1.documentName,em1.estimateNum,ds.name stateName from (select * from documentMaster where systemNum=2) d join estimateMaster em1 on (d.documentNum = em1.documentNum) join documentState ds on (d.state = ds.state) ) b on (e.documentNum = b.estimateNum)
where e.userNum=#{userNum} or b.userNum=#{userNum} or e.state='app' or b.state='app'
order by ${option}

mapper.insert:
insert into documentMaster ( documentNum, systemNum, documentTypeName, nextDocumentTypeName, userNum, userName, userDepartment, userPosition, workflowNum, state, updateDate, updater ) values( #{documentNum}, #{systemNum}, #{documentTypeName}, #{nextDocumentTypeName}, #{userNum}, #{userName}, #{userDepartment}, #{userPosition},
null,
#{workflowNum},
#{state}, CURRENT_TIMESTAMP, #{updater} );
insert into estimateMaster( documentNum, receiver, documentName ) values( #{documentNum}, #{receiver}, #{documentName} );
insert into estimateSheet1 ( documentNum, documentTypeName, userNum, userName, userDepartment, userPosition, estimateDate, supplier, address, post, phoneNumber, representative, stamp, stampFileName, logoFileName, receiver, documentName, deadline, supplyPoint, expirationDate, payCondition, cautions, sum, tax, sumWithTax, sumWithTax2, comment ) values( #{documentNum}, #{documentTypeName}, #{userNum}, #{userName}, #{userDepartment}, #{userPosition}, #{estimateDate}, #{supplier}, #{address}, #{post}, #{phoneNumber}, #{representative}, #{stamp}, #{stampFileName}, #{logoFileName}, #{receiver}, #{documentName}, #{deadline}, #{supplyPoint}, #{expirationDate}, #{payCondition}, #{cautions}, #{sum}, #{tax}, #{sumWithTax}, #{sumWithTax2}, #{comment} )

